import React, { useState, useEffect } from 'react';
import { Form, Button, Container, Row, Col, Alert, Card } from 'react-bootstrap';
import { useAuth } from '../contexts/AuthContext';
import { reportService } from '../services/reportService';
import { classService } from '../services/classService';
import { courseService } from '../services/courseService';
import ReportFormComponent from '../components/forms/ReportForm';

function ReportForm() {
  const { user } = useAuth();
  const [classes, setClasses] = useState([]);
  const [courses, setCourses] = useState([]);
  const [formData, setFormData] = useState({
    faculty_name: user?.faculty || 'ICT',
    class_id: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_id: '',
    actual_students_present: '',
    venue: '',
    scheduled_lecture_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: ''
  });
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    fetchClasses();
    fetchCourses();
  }, []);

  const fetchClasses = async () => {
    try {
      const classesData = await classService.getClasses();
      setClasses(classesData);
    } catch (error) {
      console.error('Error fetching classes:', error);
    }
  };

  const fetchCourses = async () => {
    try {
      const coursesData = await courseService.getCourses();
      setCourses(coursesData);
    } catch (error) {
      console.error('Error fetching courses:', error);
    }
  };

  const handleFormChange = (name, value) => {
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await reportService.createReport(formData);
      setMessage('Report submitted successfully!');
      setError('');
      // Reset form
      setFormData({
        faculty_name: user?.faculty || 'ICT',
        class_id: '',
        week_of_reporting: '',
        date_of_lecture: '',
        course_id: '',
        actual_students_present: '',
        venue: '',
        scheduled_lecture_time: '',
        topic_taught: '',
        learning_outcomes: '',
        recommendations: ''
      });
    } catch (error) {
      setError('Error submitting report: ' + (error.response?.data?.error || 'Unknown error'));
      setMessage('');
    }
  };

  return (
    <Container>
      <Row className="justify-content-center">
        <Col md={10}>
          <Card>
            <Card.Header>
              <h3 className="text-center">Lecturer Reporting Form</h3>
            </Card.Header>
            <Card.Body>
              {message && <Alert variant="success">{message}</Alert>}
              {error && <Alert variant="danger">{error}</Alert>}
              
              <Form onSubmit={handleSubmit}>
                <ReportFormComponent
                  formData={formData}
                  onFormChange={handleFormChange}
                  classes={classes}
                  courses={courses}
                />
                
                <div className="text-center">
                  <Button variant="primary" type="submit" size="lg">
                    Submit Report
                  </Button>
                </div>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default ReportForm;
