import React, { useState, useEffect } from 'react';
import { Modal, Button, Form, Alert, Row, Col } from 'react-bootstrap';
import { feedbackService } from '../../services/feedbackService';

function FeedbackModal({ show, onHide, report, onFeedbackSubmitted }) {
    const [formData, setFormData] = useState({
        feedback_text: '',
        rating: 0
    });
    const [existingFeedback, setExistingFeedback] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [isEditing, setIsEditing] = useState(false);

    useEffect(() => {
        if (show && report) {
            fetchExistingFeedback();
        }
    }, [show, report]);

    const fetchExistingFeedback = async () => {
        try {
            const feedback = await feedbackService.getFeedbackForReport(report.id);
            if (feedback.length > 0) {
                setExistingFeedback(feedback[0]);
                setFormData({
                    feedback_text: feedback[0].feedback_text,
                    rating: feedback[0].rating || 0
                });
                setIsEditing(true);
            } else {
                setExistingFeedback(null);
                setFormData({ feedback_text: '', rating: 0 });
                setIsEditing(false);
            }
        } catch (error) {
            console.error('Error fetching feedback:', error);
        }
    };

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleRatingChange = (rating) => {
        setFormData({
            ...formData,
            rating: rating
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            if (isEditing && existingFeedback) {
                await feedbackService.updateFeedback(existingFeedback.id, formData);
            } else {
                await feedbackService.addFeedback({
                    report_id: report.id,
                    ...formData
                });
            }
            
            if (onFeedbackSubmitted) {
                onFeedbackSubmitted();
            }
            onHide();
        } catch (error) {
            setError(error.response?.data?.error || 'Failed to submit feedback');
        } finally {
            setLoading(false);
        }
    };

    return (
        <Modal show={show} onHide={onHide} size="lg">
            <Modal.Header closeButton className="bg-warning text-dark">
                <Modal.Title>
                    {isEditing ? 'Edit Feedback' : 'Provide Feedback'} - {report?.course_name}
                </Modal.Title>
            </Modal.Header>
            <Form onSubmit={handleSubmit}>
                <Modal.Body>
                    {error && <Alert variant="danger">{error}</Alert>}
                    
                    <div className="mb-3">
                        <h6>Report Information</h6>
                        <p><strong>Course:</strong> {report?.course_code} - {report?.course_name}</p>
                        <p><strong>Lecturer:</strong> {report?.lecturer_name}</p>
                        <p><strong>Date:</strong> {report?.date_of_lecture && new Date(report.date_of_lecture).toLocaleDateString()}</p>
                        <p><strong>Topic:</strong> {report?.topic_taught}</p>
                    </div>

                    <hr />

                    <Form.Group className="mb-3">
                        <Form.Label>
                            <strong>Rating (Optional)</strong>
                        </Form.Label>
                        <div>
                            {[1, 2, 3, 4, 5].map(star => (
                                <Button
                                    key={star}
                                    type="button"
                                    variant={formData.rating >= star ? "warning" : "outline-warning"}
                                    size="sm"
                                    className="me-1"
                                    onClick={() => handleRatingChange(star)}
                                >
                                    ‚≠ê {star}
                                </Button>
                            ))}
                        </div>
                        <Form.Text className="text-muted">
                            Rate this report from 1 (Poor) to 5 (Excellent)
                        </Form.Text>
                    </Form.Group>

                    <Form.Group className="mb-3">
                        <Form.Label>
                            <strong>Feedback Comments *</strong>
                        </Form.Label>
                        <Form.Control
                            as="textarea"
                            rows={6}
                            name="feedback_text"
                            value={formData.feedback_text}
                            onChange={handleChange}
                            placeholder="Provide constructive feedback about this report. Mention strengths, areas for improvement, and any recommendations..."
                            required
                        />
                        <Form.Text className="text-muted">
                            Your feedback will help the lecturer improve their teaching and reporting.
                        </Form.Text>
                    </Form.Group>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={onHide}>
                        Cancel
                    </Button>
                    <Button 
                        variant="warning" 
                        type="submit" 
                        disabled={loading || !formData.feedback_text.trim()}
                    >
                        {loading ? 'Submitting...' : (isEditing ? 'Update Feedback' : 'Submit Feedback')}
                    </Button>
                </Modal.Footer>
            </Form>
        </Modal>
    );
}

export default FeedbackModal;
