import React, { useState, useEffect } from 'react';
import { Container, Card, Table, Alert } from 'react-bootstrap';
import { useAuth } from '../contexts/AuthContext';
import { classService } from '../services/classService';

function Classes() {
  const { user } = useAuth();
  const [classes, setClasses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchClasses();
  }, []);

  const fetchClasses = async () => {
    try {
      const classesData = await classService.getClasses();
      setClasses(classesData);
    } catch (error) {
      setError('Error fetching classes: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <Container>
        <div className="text-center">Loading classes...</div>
      </Container>
    );
  }

  return (
    <Container>
      <Card>
        <Card.Header>
          <h3>Classes</h3>
        </Card.Header>
        <Card.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          
          <Table responsive striped bordered hover>
            <thead className="table-dark">
              <tr>
                <th>Class Name</th>
                <th>Course</th>
                <th>Lecturer</th>
                <th>Total Students</th>
                <th>Venue</th>
                <th>Scheduled Time</th>
              </tr>
            </thead>
            <tbody>
              {classes.map(cls => (
                <tr key={cls.id}>
                  <td>{cls.class_name}</td>
                  <td>
                    <div><strong>{cls.course_code}</strong></div>
                    <small className="text-muted">{cls.course_name}</small>
                  </td>
                  <td>{cls.lecturer_name}</td>
                  <td>{cls.total_registered_students}</td>
                  <td>{cls.venue}</td>
                  <td>{cls.scheduled_time}</td>
                </tr>
              ))}
            </tbody>
          </Table>
          
          {classes.length === 0 && (
            <div className="text-center text-muted py-4">
              <h5>No classes found</h5>
              <p>There are no classes assigned to you at the moment.</p>
            </div>
          )}
        </Card.Body>
      </Card>
    </Container>
  );
}

export default Classes;
