import React, { useState, useEffect } from 'react';
import { Container, Card, Row, Col, InputGroup, Form, Button, Alert, Modal } from 'react-bootstrap';
import { useAuth } from '../contexts/AuthContext';
import { reportService } from '../services/reportService';
import ReportsTable from '../components/tables/ReportsTable';
import SearchBar from '../components/common/SearchBar';
import ExportButton from '../components/common/ExportButton';
import FeedbackModal from '../components/modals/FeedbackModal';

function Reports() {
  const { user } = useAuth();
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [selectedReport, setSelectedReport] = useState(null);
  const [showViewModal, setShowViewModal] = useState(false);
  const [showFeedbackModal, setShowFeedbackModal] = useState(false);

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      setLoading(true);
      const reportsData = await reportService.getReports();
      setReports(reportsData);
      setError('');
    } catch (error) {
      console.error('Error fetching reports:', error);
      setError('Failed to load reports');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (term) => {
    setSearchTerm(term);
    if (!term.trim()) {
      fetchReports();
      return;
    }
    
    try {
      const results = await reportService.searchReports(term);
      setReports(results);
      setError('');
    } catch (error) {
      console.error('Error searching reports:', error);
      setError('Search failed');
    }
  };

  const handleExport = async () => {
    if (!startDate || !endDate) {
      alert('Please select both start and end dates');
      return;
    }
    
    try {
      const blob = await reportService.exportReports(startDate, endDate);
      const url = window.URL.createObjectURL(new Blob([blob]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `reports-${startDate}-to-${endDate}.xlsx`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Error exporting reports:', error);
      alert('Error exporting reports: ' + (error.response?.data?.error || 'Unknown error'));
    }
  };

  const handleViewReport = (report) => {
    setSelectedReport(report);
    setShowViewModal(true);
  };

  const handleFeedback = (report) => {
    setSelectedReport(report);
    setShowFeedbackModal(true);
  };

  const handleFeedbackSubmitted = () => {
    // Refresh reports or show success message
    setError('');
    alert('Feedback submitted successfully!');
  };

  // ... (keep the existing ViewReportModal component)

  if (loading) {
    return (
      <Container>
        <div className="text-center py-4">Loading reports...</div>
      </Container>
    );
  }

  return (
    <Container>
      <Card>
        <Card.Header>
          <h3 className="mb-0">Reports</h3>
        </Card.Header>
        <Card.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          
          {/* Search and Export Section */}
          <Row className="mb-4">
            <Col md={6}>
              <SearchBar
                onSearch={handleSearch}
                placeholder="Search reports by course, topic, or lecturer..."
                size="lg"
              />
            </Col>
            <Col md={6}>
              <InputGroup size="lg">
                <Form.Control
                  type="date"
                  placeholder="Start Date"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                />
                <Form.Control
                  type="date"
                  placeholder="End Date"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                />
                <ExportButton onExport={handleExport} size="lg" />
              </InputGroup>
            </Col>
          </Row>

          {/* Reports Table */}
          <ReportsTable
            reports={reports}
            onView={handleViewReport}
            onFeedback={handleFeedback}
            userRole={user.role}
          />

          {reports.length === 0 && !loading && (
            <div className="text-center text-muted py-4">
              <h5>No reports found</h5>
              <p>There are no reports to display at the moment.</p>
              {user.role === 'lecturer' && (
                <Button variant="primary" href="/report-form">
                  Create Your First Report
                </Button>
              )}
            </div>
          )}
        </Card.Body>
      </Card>

      {/* View Report Modal */}
      <ViewReportModal
        show={showViewModal}
        onHide={() => setShowViewModal(false)}
        report={selectedReport}
      />

      {/* Feedback Modal */}
      <FeedbackModal
        show={showFeedbackModal}
        onHide={() => setShowFeedbackModal(false)}
        report={selectedReport}
        onFeedbackSubmitted={handleFeedbackSubmitted}
      />
    </Container>
  );
}

export default Reports;
