import React, { useState, useEffect } from 'react';
import { Container, Table, Card, Form, Button, Row, Col, InputGroup } from 'react-bootstrap';
import { useAuth } from '../contexts/AuthContext';
import { reportService } from '../services/reportService';
import ReportsTable from '../components/tables/ReportsTable';
import SearchBar from '../components/common/SearchBar';
import ExportButton from '../components/common/ExportButton';

function Reports() {
  const { user } = useAuth();
  const [reports, setReports] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      const reportsData = await reportService.getReports();
      setReports(reportsData);
    } catch (error) {
      console.error('Error fetching reports:', error);
    }
  };

  const handleSearch = async (term) => {
    setSearchTerm(term);
    if (!term) {
      fetchReports();
      return;
    }
    
    try {
      const results = await reportService.searchReports(term);
      setReports(results);
    } catch (error) {
      console.error('Error searching reports:', error);
    }
  };

  const handleExport = async () => {
    if (!startDate || !endDate) {
      alert('Please select both start and end dates');
      return;
    }
    
    try {
      const blob = await reportService.exportReports(startDate, endDate);
      const url = window.URL.createObjectURL(new Blob([blob]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `reports-${startDate}-to-${endDate}.xlsx`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      console.error('Error exporting reports:', error);
      alert('Error exporting reports');
    }
  };

  const handleViewReport = (report) => {
    // Implement view report functionality
    console.log('View report:', report);
    alert(`Viewing report for ${report.course_name}`);
  };

  const handleFeedback = (report) => {
    // Implement feedback functionality
    console.log('Provide feedback for:', report);
    alert(`Providing feedback for ${report.course_name}`);
  };

  return (
    <Container>
      <Card>
        <Card.Header>
          <h3>Reports</h3>
        </Card.Header>
        <Card.Body>
          {/* Search and Export Section */}
          <Row className="mb-3">
            <Col md={6}>
              <SearchBar
                onSearch={handleSearch}
                placeholder="Search reports by course, topic, or lecturer..."
              />
            </Col>
            <Col md={6}>
              <InputGroup>
                <Form.Control
                  type="date"
                  placeholder="Start Date"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                />
                <Form.Control
                  type="date"
                  placeholder="End Date"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                />
                <ExportButton onExport={handleExport} />
              </InputGroup>
            </Col>
          </Row>

          {/* Reports Table */}
          <ReportsTable
            reports={reports}
            onView={handleViewReport}
            onFeedback={handleFeedback}
            userRole={user.role}
          />
        </Card.Body>
      </Card>
    </Container>
  );
}

export default Reports;
